package net.jeeshop.web.action.manage.emailNotifyProduct;import java.io.IOException;import org.slf4j.LoggerFactory;import net.jeeshop.core.BaseAction;import net.jeeshop.services.manage.emailNotifyProduct.EmailNotifyProductService;import net.jeeshop.services.manage.emailNotifyProduct.bean.EmailNotifyProduct;import net.jeeshop.services.manage.emailNotifyProduct.impl.EmailNotifyProductServiceImpl;public class EmailNotifyProductAction extends BaseAction<EmailNotifyProduct> {	private static final long serialVersionUID = 1L;	private static final org.slf4j.Logger logger = LoggerFactory.getLogger(EmailNotifyProductAction.class);	private EmailNotifyProductService emailNotifyProductService;	public EmailNotifyProductService getEmailNotifyProductService() {		return emailNotifyProductService;	}	protected void selectListAfter() {		pager.setPagerUrl("emailNotifyProduct!selectList.action");	}	public void setEmailNotifyProductService(			EmailNotifyProductService emailNotifyProductService) {		this.emailNotifyProductService = emailNotifyProductService;	}	public EmailNotifyProduct getE() {		return this.e;	}	public void prepare() throws Exception {		if (this.e == null) {			this.e = new EmailNotifyProduct();		}	}	public void insertAfter(EmailNotifyProduct e) {		e.clear();	}		public String autoNotify() throws IOException{		logger.error("autoNotify...");		emailNotifyProductService.autoNotify();		super.write("success");		return null;	}}